import React, { memo, useState } from "react";
import { View, Text, StyleSheet,Picker, TouchableOpacity } from "react-native";
import Background from "../components/Background";
import Logo from "../components/Logo";
import Button from "../components/Button";
import TextInput from "../components/TextInput";
import BackButton from "../components/BackButton";
import { theme } from "../core/theme";
import Toast from 'react-native-tiny-toast';
import * as firebase from 'firebase';
import { Header } from 'react-native-elements';
import   FIREBASE_CONFIG   from "../core/config";
import { Dropdown } from 'react-native-material-dropdown';

if (!firebase.apps.length) {
  firebase.initializeApp(FIREBASE_CONFIG);
}

const PriorityIndex = ({ navigation }) => {
  const [Os, setOs] = useState({ value: ""});
  const [Power, setPower] = useState({ value: ""});
  const [Softwere, setSoftwere] = useState({ value: ""});
  const [Hardware, setHardware] = useState({ value: ""});
  const [Others, setOthers] = useState({ value: ""});
  const _onUpdatePressed = ()=> {
    console.log("os ",Os.value,Hardware.value,Softwere.value,Power.value,Others.value)
    firebase.database().ref('Priority/').set({
      1:Os.value,
      2:Power.value,
      3:Softwere.value,
      4:Hardware.value,
      5:Others.value
    });

    Toast.showSuccess('Priority updated')  
  };
 let itemli=[{
  value: 'OS',
}, {
  value: 'SERVER',
}, {
  value: 'HARDWARE',
 },{
  value: 'SOFTWERE',
}, {
  value: 'POWER',
}, {
  value: 'OTHER',
 }

];




  return (
    <Background>
    <Header
  backgroundColor='#F6820D'
  leftComponent={<BackButton goBack={() => navigation.navigate("AdminDashboard")} />}
  centerComponent={{text:'PRIORITY INDEX'}}/>
     <Dropdown
        label='CATAGORY'
        data={itemli}
      />

      <TextInput
        label="OS"
        returnKeyType="next"
        value={Os.value}
        onChangeText={text => setOs({ value: text})}
      />
      <Picker
     //   selectedValue={Os.value}
        style={{ height: 50, width: 150 }}
   //     onValueChange={(itemValue, itemIndex) => setSelectedValue(itemValue)}
      >
        <Picker.Item label="Java" value="java" />
        <Picker.Item label="JavaScript" value="js" />
      </Picker>

      <TextInput
        label="Others"
        returnKeyType="next"
        value={Others.value}
        onChangeText={text => setOthers({ value: text})}
      />

      <TextInput
        label="Power"
        returnKeyType="next"
        value={Power.value}
        onChangeText={text => setPower({ value: text})}
      />
      <TextInput
        label="Softwere"
        returnKeyType="next"
        value={Softwere.value}
        onChangeText={text => setSoftwere({ value: text})}
      />

             <Button
        mode="contained"
        onPress={_onUpdatePressed}
        style={styles.button}
      >
        UPDATE PRIORITY
      </Button>

    </Background>
  );
};

const styles = StyleSheet.create({

  button: {
    marginTop: 24
  },
  row: {
    flexDirection: "row",
    marginTop: 4
  },
  link: {
    fontWeight: "bold",
    color: theme.colors.primary
  }
});

export default memo(PriorityIndex);
