import React, { memo ,Component} from "react";
import Background from "../components/Background";
import { TouchableOpacity,Image, Dimensions,StyleSheet, Text, View,FlatList,ScrollView, Platform, AlertScrollView, TextBase, Alert} from "react-native";
import { Card } from 'react-native-elements'

import {
    LineChart,
    BarChart,
    PieChart,
    ProgressChart,
    ContributionGraph,
    StackedBarChart
  } from 'react-native-chart-kit'
import * as firebase from 'firebase';
import { InputAccessoryView } from "react-native";


// Initialize Firebase
const firebaseConfig = {
  apiKey: "AIzaSyDslHhZc8QSFrsonWJlsvBXBm3vesReV84",
  authDomain: "complaint-a4db2.firebaseapp.com",
  databaseURL: "https://complaint-a4db2.firebaseio.com",
  projectId: "complaint-a4db2",
  storageBucket: "complaint-a4db2.appspot.com",
  messagingSenderId: "966569668349",
  appId: "1:966569668349:web:24e2908bf9f3b5ffd6d894",
  measurementId: "G-VQXSNP4LCQ"
};

if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
}

export default class AdminViualrep extends Component{  
  constructor(){
    super();
    this.state={
      data1:[],
    }

  }
  componentDidMount()
  { 
    firebase.database().ref('Form/').once('value', (snapshot)=> {
    var CountHardware=0;
    var CountSoftwere=0;
    var CountServer=0;
    var CountPower=0;
    var CountOs=0;
    var CountOthers=0;
    let rdata = snapshot.val();
    var ritems = Object.values(rdata);
      console.log("counting data");

   for(let i=0;i<ritems.length;i++){
       if (ritems[i].Catagory==="SERVER"){
           CountServer+=1
       }
       else if(ritems[i].Catagory==="SOFTWERE"){
        CountSoftwere+=1
       }
       else if(ritems[i].Catagory==="OS"){
        CountOs+=1
      }else if(ritems[i].Catagory==="POWER"){
        CountPower+=1
      }else if(ritems[i].Catagory==="HARDWARE"){
        CountHardware+=1
      }
      else if(ritems[i].Catagory==="OTHER"){
        CountOthers+=1
      }
    }
    this.setState({
      data1:[...this.state.data1,{CountHardware,CountSoftwere,CountServer, CountPower, CountOs, CountOthers}],
    })
    console.log("Counted=",CountOthers,CountPower,CountServer,CountSoftwere,CountHardware)
    console.log("data=",this.state.data1[0].CountHardware,this.state.data1[0].CountSoftwere,this.state.data1[0].CountServer,this.state.data1[0].CountPower,this.state.data1[0].CountOs,this.state.data1[0].CountOthers)

    });


  }


  render(){
    barData = {
      labels: ['Hardware', 'Softwere', 'Server', 'Power', 'Os', 'Others'],
      datasets: [
        {
          data:this.sata,
        },
      ],
      }
     chartConfig1={
      backgroundColor: '#e26a00',
        backgroundGradientFrom: '#fb8c00',
        backgroundGradientTo: '#ffa726',
        decimalPlaces: 2, // optional, defaults to 2dp
        color: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
        style: {
          borderRadius: 16
        }

      }
    return (
      <View>
        <Card>
        <BarChart
    // style={graphStyle}
    data={barData}
    width={Dimensions.get('window').width}
    height={220}
    yAxisLabel={''}
    chartConfig={chartConfig1}>
    </BarChart>
      </Card>
        </View>
  );
  } 
};
const styles = StyleSheet.create({
  image: {
    width: 40,
    height: 40
  }
});
