export default class AdminList extends Component{  
  constructor(){
    super();
    this.state={
      data:[]
    }
    firebase.database().ref('Form/').on('value', (snapshot)=> {
      let data = snapshot.val();
      var items = Object.values(data);
      console.log("object javascript");
      console.log(data);
      for(let i=0;i<items.length;i++){
        this.setState({
          data:[...this.state.data,{Id:items[i]}],
        })
      }
      console.log(this.state.data)
      console.log(this.state.data[0].Id.DOS)
    }); 
  }
  render(){
    return (
      <View  style={styles.container}>

      <Header> LIST OF ISSUES</Header>

      {
      this.state.data.map((item, i) => (
      <ListItem 
        key={i}
        title={item.Id.Id}
        subtitle={item.Id.Catagory}
        leftIcon={{name:'av-timer'}}
        bottomDivider
        onPress={()=>infoFloat(item.Id.Id)}
        chevron
      />
    ))
  }
        </View>
  );
  } 
};
const styles = StyleSheet.create({
